/* Placeholder local bootstrap bundle.
   Loads bundle from CDN. Replace with offline file for fully local usage. */
fetch('https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js')
  .then(resp => resp.text())
  .then(code => { const s = document.createElement('script'); s.type='text/javascript'; s.text = code; document.head.appendChild(s);})
  .catch(()=> console.warn('Could not load bootstrap bundle from CDN.'));
